var url = pm.variables.get("urlToken");

var hashToken = "";

if (pm.environment.get("ClientId") == undefined) {
  hashToken = btoa(
    pm.collectionVariables.get("ClientId") + ":" + pm.collectionVariables.get("ClientSecret")
  );
} else {
  hashToken = btoa(
    pm.environment.get("ClientId") + ":" + pm.environment.get("ClientSecret")
  );
}

const createOptions = {
  url: `${url}/token-service/oauth/v2/tokenization/`,
  method: "POST",
  header: {
    "Content-Type": "application/json",
    Authorization: 'Bearer ' + pm.variables.get('access_token')
  }, 
  body: {
    mode: "raw",
    raw: JSON.stringify({
      email: "john_snow@gmail.com",
      cardNumber: "0000000000000008",
      expirationMonth: `${pm.variables.get("current_month")}`,
      expirationYear: `${pm.variables.get("current_year")}`,
      securityCode: "235",
      storageCard: 2,
    }),
  },
};

pm.sendRequest(createOptions, function (err, res) {
  if (res.code == 201) {
    console.log(res.json().tokenizationId);
    const tokenizationId = res.json().tokenizationId;
    pm.variables.set("tokenizationId", tokenizationId);

    const callbackOptions = {
      url: `${url}/token-service/v2/tokenization/callback/${pm.variables.get(
        "tokenizationId"
      )}`,
      method: "PUT",
      header: {
        "Content-Type": "application/json",
      },
    };

    pm.sendRequest(callbackOptions, function (err, resp) {
      if (resp.code == 201) {
        console.log(res.json().tokenizationId);
        const tokenizationId = res.json().tokenizationId;
        pm.variables.set("tokenizationId", tokenizationId);
        console.log("Callback done");
      }
    });
  }
});
